import React, { Component } from 'react';
import './App.css';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Logo from './components/Logo/Logo';
import Navigation from './components/Navigation/Navigation';
import Rank from './components/Rank/Rank';
import ParticlesBg from 'particles-bg'


import Clarifai from 'clarifai';

const app = new Clarifai.App({
  apiKey: '33ae34510ce844dd81ba2294c21d84c4'
});

export const detectarFacesNaImagem = (urlDaImagem) => {
  return app.models.predict(Clarifai.FACE_DETECT_MODEL, urlDaImagem)
    .then(response => {
      return response.outputs[0].data.regions;
    })
    .catch(err => console.log(err));
};



class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
    }
  }

  onInputChange = (event) => {
    console.log(event.target.value);
  }

  onButtonSubmit = () => {
    const { input } = this.state;
    detectarFacesNaImagem(input)
  }

  render() {
    return (
      <>
        <div className='App'>
          <Navigation />
          <Logo />
          <Rank />
          <ImageLinkForm 
            onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit} 
          />
          { /* <FaceRecognition /> */ }
        </div>
        <div className='Particles'>
          <ParticlesBg type="cobweb" num={300} color='#ffffff' bg={true} />
        </div>
      </>
    );
  }
}

export default App;
