import React, { Component } from 'react';
import './App.css';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Logo from './components/Logo/Logo';
import Navigation from './components/Navigation/Navigation';
import Rank from './components/Rank/Rank';
import ParticlesBg from 'particles-bg';
import Clarifai from 'clarifai'


const app = new Clarifai.App ({
  apiKey: '33ae34510ce844dd81ba2294c21d84c4'
})

const detectFace = async (imageUrl) => {
  try {
    const response = await fetch('http://localhost:5000/imageurl', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        input: imageUrl
      })
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
    return error;
  }
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
    }
  }

  onInputChange = (event) => {
    // console.log(event.target.value);
    this.setState({ input: event.target.value });
  }

  onUrlSubmit = () => {
    this.onButtonSubmit(this.state.input);
  }  

  onButtonSubmit = (imageUrl) => {
    return detectFace(imageUrl)
      .then(response => {
        console.log(response);
      })
      .catch(err => console.log(err));
  }


  render() {
    return (
      <>
        <div className='App'>
          <Navigation />
          <Logo />
          <Rank />
          <ImageLinkForm 
            onInputChange={this.onInputChange} 
            onButtonSubmit={this.onButtonSubmit} 
          />
          {/* <FaceRecognition 
          /> */}
        </div>
        <div className='Particles'>
          <ParticlesBg type="cobweb" num={300} color='#ffffff' bg={true} />
        </div>
      </>
    );
  }
}

export default App;
